var imgs=[];var $currentImg;$(function(){var a=$("#galleryContainer .item img").map(function(){return $(this).attr("src")});imgs=a.toArray()});function showPreview(a){$currentImg=a;$("#wrapper").css("overflow","hidden");$($("#overlayGallery #previewList .item img")[0]).attr("src",$currentImg);$("#overlayGallery").show()}function nextImg(){var a=imgs.indexOf($currentImg);if(a==imgs.length-1){closePreview()}else{showPreview(imgs[++a])}}function prevImg(b){var a=imgs.indexOf($currentImg);if(a==0){closePreview()}else{showPreview(imgs[--a])}}function closePreview(){$("#wrapper").css("overflow","auto");$("#overlayGallery").hide()};